import UIKit

extension ForgotPasswordViewController {
    func setupSubviews() {
        self.setupNavigationBar()
        self.setupNavigationItem()
        self.setupContentView()
        self.setupTableView()
    }
    
    func showNavigationBar() {
        self.navigationController?.setNavigationBarHidden(false, animated: false)
    }
    
    private func setupNavigationBar() {
        self.navigationController?.navigationBar.isTranslucent = ForgotPasswordStyle.shared.navigationBarModel.isTranslucent
        self.navigationController?.navigationBar.tintColor = ForgotPasswordStyle.shared.navigationBarModel.tintColor
        self.navigationController?.navigationBar.barTintColor = ForgotPasswordStyle.shared.navigationBarModel.barTintColor
        self.navigationController?.navigationBar.titleTextAttributes = ForgotPasswordStyle.shared.navigationBarModel.titleAttributes()
    }
    
    private func setupNavigationItem() {
        self.navigationItem.title = ForgotPasswordLocalization.shared.forgotPasswordTitle
    }
    
    private func setupContentView() {
        self.view.backgroundColor = ForgotPasswordStyle.shared.contentViewModel.backgroundColor
    }
    
    private func setupTableView() {
        self.tableView?.separatorStyle = .none
        self.tableView?.register(ForgotPasswordTextFieldCell.self, forCellReuseIdentifier: ForgotPasswordTextFieldCell.defaultReuseIdentifier)
        self.tableView?.register(ForgotPasswordButtonCell.self, forCellReuseIdentifier: ForgotPasswordButtonCell.defaultReuseIdentifier)
    }
}
