import UIKit

class FullscreenImageStyle {
    static let shared = FullscreenImageStyle()
    
    var contentViewModel: ContentViewModel
    var dismissButtonModel: DismissButtonModel
    var loadingImageViewModel: LoadingImageViewModel
    
    private init() {
        self.contentViewModel = ContentViewModel()
        self.dismissButtonModel = DismissButtonModel()
        self.loadingImageViewModel = LoadingImageViewModel()
    }
    
    struct ContentViewModel {
        var backgroundColor = ApplicationStyle.colors.primaryLight
    }
    
    struct DismissButtonModel {
        var image: UIImage = ApplicationStyle.images.closeIcon
        var tintColor: UIColor = ApplicationStyle.colors.primary
    }
    
    struct LoadingImageViewModel {
        var activityIndicatorColor: UIColor = ApplicationStyle.colors.primary
        var placeholderImage: UIImage? = ApplicationStyle.images.placeholderImageIcon
    }
}
