@testable import Travelling
import XCTest

class FullscreenImageRouterTests: XCTestCase {
    var sut: FullscreenImageRouter!
    var viewControllerSpy: FullscreenImageViewControllerSpy!
    
    // MARK: - Test lifecycle
    
    override func setUp() {
        super.setUp()
        self.setupFullscreenImageRouter()
    }
    
    override func tearDown() {
        super.tearDown()
    }
    
    // MARK: - Test setup
    
    func setupFullscreenImageRouter() {
        self.sut = FullscreenImageRouter()
        
        self.viewControllerSpy = FullscreenImageViewControllerSpy()
        self.sut.viewController = self.viewControllerSpy
    }
    
    // MARK: - Tests
    
    func testDismissViewController() {
        self.sut.dismissViewController()
        XCTAssertTrue(self.viewControllerSpy.dismissCalled)
    }
}
