@testable import Travelling
import XCTest

class OnboardingInteractorTests: XCTestCase {
    var sut: OnboardingInteractor!
    var presenterSpy: OnboardingPresentationLogicSpy!
    var workerSpy: OnboardingWorkerSpy!
  
    // MARK: - Test lifecycle
  
    override func setUp() {
        super.setUp()
        self.setupOnboardingInteractor()
    }
  
    override func tearDown() {
        super.tearDown()
    }
  
    // MARK: - Test setup
  
    func setupOnboardingInteractor() {
        self.sut = OnboardingInteractor()
        
        self.presenterSpy = OnboardingPresentationLogicSpy()
        self.sut.presenter = self.presenterSpy
        
        self.workerSpy = OnboardingWorkerSpy(delegate: self.sut)
        self.sut.worker = self.workerSpy
    }
    
    // MARK: - Tests
  
    
}
